import { useState } from 'react';

// components
import Header from './components/Header';
import Body from './components/Body';
import Footer from './components/Footer';

function App() {
  const [theme, setTheme] = useState('one');

  const currTheme =
    theme === 'one'
      ? 'bg-one-bg-primary'
      : theme === 'two'
      ? 'bg-two-bg-primary'
      : 'bg-three-bg-primary';

  return (
    <div
      className={`grid min-h-screen grid-rows-[1fr_auto]  font-spartan ${currTheme}`}
    >
      <main className=" mx-auto grid w-[90%] max-w-[40rem] md:max-w-[42rem] grid-rows-[3rem_5.5rem_26rem] content-center gap-6 py-8 md:grid-rows-[3rem_8rem_30rem]">
        <Header theme={theme} setTheme={setTheme} />

        <Body theme={theme} />
      </main>

      <Footer theme={theme} />
    </div>
  );
}

export default App;
